/* Don't return values in an Alert just display the results on the page */
document.addEventListener("DOMContentLoaded", function () {
var inputText = document.createElement('input');
inputText.type = 'text';
inputText.id = 'inputText';
document.body.appendChild(inputText);
var button = document.createElement('button');
button.innerHTML = 'Click me';
button.onclick = function () {
    var text = document.getElementById('inputText').value;
    var words = text.split(' ');
    var longWords = 0;
    var averageWords = 0;
    for (var i = 0; i < words.length; i++) {
        if (words[i].length > 6) {
            longWords++;
        }
    }
    var sentences = text.split('.');
    for (var i = 0; i < sentences.length; i++) {
        averageWords += sentences[i].split(' ').length;
    }
    averageWords = averageWords / sentences.length;
    var percentageOfLongWords = document.createElement('div');
    percentageOfLongWords.innerHTML = 'Percentage of long words: ' + (longWords / words.length * 100) + '%';
    document.body.appendChild(percentageOfLongWords);
    var averageWordsPerSentence = document.createElement('div');
    averageWordsPerSentence.innerHTML = 'Average number of words per sentence: ' + averageWords;
    document.body.appendChild(averageWordsPerSentence);
    var lixFormula = document.createElement('div');
    lixFormula.innerHTML = 'Lix formula: ' + (words.length / sentences.length + (longWords * 100) / words.length);
    document.body.appendChild(lixFormula);
    var rixFormula = document.createElement('div');
    rixFormula.innerHTML = 'Rix formula: ' + (longWords / sentences.length);
    document.body.appendChild(rixFormula);
};
    document.body.appendChild(button);
});